INSERT INTO `tgt_nhac_data` (`m_id`, `m_title`, `m_title_ascii`, `m_singer`, `m_poster`, `m_sang_tac`, `m_kbs`, `m_cat`, `m_url`, `m_is_local`, `m_lyric`, `m_type`, `m_viewed`, `m_viewed_month`, `m_downloaded`, `m_downloaded_month`, `m_time`, `m_is_broken`, `m_hot`, `m_img`, `m_hq`, `m_mempost`, `m_lyricSRT`, `m_lyricCaption`, `m_lyricLRC`, `m_lyricLRCNCT`, `m_lyricKAR`, `m_lyricZSTAR`, `m_lyricLRCZMP3`, `m_imgNCT`, `m_urlGRAB`, `m_url2`, `m_url3`, `m_url4`) VALUES
(365, 'Liên Khúc Ai Cho Tôi Tình Yêu (mboxmp3.com)', 'lien khuc ai cho toi tinh yeu (mboxmp3.com)', 225, 1, '', '', ',1,4,5,', 'http://mboxmp3.com/u/Lien Khuc Tuan Vu 1 - Tuan Vu [MP3 128kbps].mp3', 0, 'Ai cho tôi tình yêu để làm duyên nụ cười\r\nTôi xin dâng vòng tay mở rộng\r\nVà đón người đi vào tim tôi\r\nBằng môi trên bờ môi\r\n\r\nNhưng biết chỉ là mơ nên lòng nức nở\r\nThương còn đi chứ yêu thì chưa đến\r\nTên gọi tên tình chưa đỗ bến\r\nBiết nẻo mô mà tìm\r\n\r\nTám hướng bốn phương trời mây\r\nThôi nhé anh đi từ đây\r\nKỷ niệm nào không có vui hay buồn\r\nChiều không có hoàng hôn\r\nTình nào hơn nước non\r\n\r\nRồi đây một mai lối xưa tôi về\r\nKể chuyện buồn vui hai đứa mình nghe\r\nMột gian nhà xinh giấc mơ đềm êm\r\nLắng nghe tiếng nhịp con tim\r\nHai người gọi chung một tên\r\n\r\nEm có về, về ngõ xưa\r\nEm, em có còn, còn từng đêm\r\nEm đếm sao cho tình mình\r\nThương mảnh trăng khuyết che mây mờ.\r\n\r\nBuồn nào hơn đêm nay buồn nào hơn đêm nay\r\nKhi ngoài kia bão tố đầy trời\r\nTừng cánh lá cuốn gió trôi vào lòng đêm thâu\r\nThương [thầm mối tình ngâu\r\nNgày về ôi xa quá cánh nhạn còn miệt mài\r\nTrong nắng hồng mê say lạc bầy chim chíu chít\r\nHai phương trời cách biệt đêm chờ và đêm mong.\r\n\r\nNgười đi đi ngoài phố, chiều nắng tắt bên sông\r\nNgười đi đi ngoài phố, nhớ dáng xưa mịt mù\r\nThành ghế đá chiều công [viên\r\nNgày xưa, ngày xưa, ngày xưa đã hết rồi\r\n\r\nNgười đi đi ngoài phố, chiều bỡ ngỡ bơ vơ\r\nNgười đi đi ngoài phố, mấy dấu chân lạc loài\r\nHình bóng cũ người yêu [ơi\r\nCòn đâu, còn đâu tình duyên đã lỡ rồi\r\n\r\nSao mùa xuân không đến khi đông tàn\r\nSao hồn ta như chiếc lá thu vàng\r\nKiếp cô đơn chập chờn buông\r\nTrong bóng đêm dài chỉ một mình ta với ta.\r\n\r\nÔi còn đâu giây phút yêu ban đầu\r\nCung đàn xưa vương vấn mối tơ sầu\r\nNhớ chăng em lời thề xưa\r\nAi nỡ vô tình đành lìa xa bến mơ.\r\n\r\nNửa đêm nói chuyện chúng mình yêu nhau\r\nMột vầng trăng khuyết lạc trên tuyến đầu\r\nEm hỏi sau này chúng mình thành đôi\r\nNhư lúc trăng vơi nhưng rồi lại đầy\r\nThì tương lai đó có anh và em\r\nGhép tên chung thiệp hồng\r\n\r\nSuốt đêm không ngủ bên tách cà phê đen chúng ta ôn chuyện đời\r\nNgày mình còn thơ in như hình với bóng phút giây chưa hề rời\r\nXuân qua mấy lần chôn kín chuyện xa xưa\r\nTình cờ gặp nhau hai đứa cùng đơn vị không ngăn được nỗi mừng\r\nAnh kể tôi nghe ngày anh mới ra trường.\r\n\r\nRồi một hôm tôi gặp nàng\r\nĐem tiếng hát cung đàn với niềm yêu lai láng.\r\nNhưng than ôi quá bẽ bàng bao tiếng hát cung đàn\r\nNgười chẳng màng còn chê chán\r\n\r\nNhìn đời thấy lắm phũ phàng\r\nMượn tiếng hát cung đàn, với niềm đau dĩ vãng\r\nNhưng bao giông tố lan tràn, lên gác tía huy hoàng\r\nXiêu đổ theo nước mắt nàng\r\n\r\nCòn đâu đâu lá ngọc cành vàng, còn đâu đâu quyền quý cao sang,\r\nEm chính em ngày xưa đó, đến bây giờ phiêu bạt giữa trần gian\r\nÐời tôi vẫn tiếng nhạc cung đàn, đời tôi vẫn nghệ sĩ lang thang\r\nEm, em nhớ xưa rồi em khóc tôi thoáng buồn thương dòng lệ đài trang.\r\n\r\nTừ khi hoa lá tô thắm cuộc đời\r\nTừ khi manh áo sưởi ấm lòng người\r\nTìm trong hơi gió và trong tiếng tiêu\r\nTôi viết lên khúc nhạc chiều để ca ngợi tình yêu.\r\n\r\nMột yêu non nước như mối duyên đầu\r\nVà yêu quê hương mảnh đất đẹp màu\r\nVà tha thiết nữa là em với tôi\r\nKhi đã cho nhau cuộc đời mặn nồng đôi có đôi.\r\n\r\nHôm nay tôi trở về thăm trường cũ\r\nNhiều nét đổi thay tường mái rêu mờ\r\nBên hiên hằng giờ tìm những bạn xưa\r\nMay ra có còn đôi đứa\r\nVẫn yên vui sống đời học trò\r\n\r\nBâng khuâng đợi chờ người sao chẳng đến\r\nHỏi lá hỏi hoa chỉ thấy im lìm\r\nCây dương đầu trường còn khắc hàng tên\r\nHoa leo phũ phàng giăng kín\r\nTiếng ve ru nghe gợi buồn thêm\r\n\r\nCuộc tình đó đã khuất xa tầm tay\r\nTiếng hát em còn đây tơ vương lệ thấm đầy\r\nHồn còn say men ái ân chiều ấy\r\nBóng dáng em từ nay đã khuất xa trời mây\r\n\r\nEm đi rồi thành phố hôm nay\r\nMưa giăng đầy lạnh buốt tim này\r\nEm đi rồi kỷ niệm xưa còn đấy\r\nEm đi mất rồi còn nhớ chăng tình này.\r\n\r\nNgười về, một mùa thu gió heo may\r\nVề đâu có nhớ chăng những vì sao long lanh\r\nĐưa [tiễn người một đêm không trăng\r\nNói sao nên lời lòng buồn như chiều rơi\r\nNhư trong đêm khuya những buớc chân qua [thềm\r\nGợi niềm thương nhớ vô vàn.\r\n\r\nNgười về, đường đi kết gió trăng sao\r\nNgười đi có biết chăng trong chiều nay bơ vơ\r\nNghe lá thu vàng rơi bâng khuâng\r\nBước chân ai về chừng thời gian ngừng trôi\r\nNhư quên đêm khuya để gió xuôi theo [mùa\r\nThầm làm ướt áo vai gầy.\r\n\r\nTừ ngày có em về, nhà mình toàn ánh trăng thề.\r\nDòng nhạc tình đang tắt lâu, tuôn trào ngọt ngào như dòng suối\r\nAnh yêu phút ban đầu, đẹp nghiêng nghiêng dáng em sầu.\r\nTrong mắt em buồn về mau, em ơi có khi nào lần gặp đây cho mai sau.\r\n\r\nNgày anh gặp đôi mắt long lanh\r\nLần đầu anh lạc trong hố mê say\r\nDường như ta quen nhau từ muôn kiếp\r\nVừa gặp lại ta đã sợ tình cao bay\r\n\r\nTừ khi anh yêu em là anh biết đợi chờ\r\nChờ cho mau qua đêm dài tăm tối\r\nMình yêu nhau đi thôi đừng lo phút chia tay\r\nChỉ có em lòng vui như hôm nay\r\n\r\nBuổi chiều sông Seine có gió lạnh về lập đông\r\nBuổi chiều Cali cô đơn từng cơn rã rời\r\nTừ ngày xa em thao thức trằn trọc từng đêm\r\nNgày rời Paris anh đã để quên con tim.\r\n\r\nBuồn ơi sao còn đến lòng đã quá sầu đau\r\nAi đã đem chuyện xưa lời hứa ban đầu chôn kín vào tim\r\nNgày xưa nay còn đâu tình xưa biết tìm đâu\r\nThôi đón đưa mà chi tình đã không thành còn ước mơ gì.\r\n\r\nNhưng cớ sao em buồn tình ta ước hẹn nhiều rồi\r\nNgày [mai ấm lại cuộc đời kể chuyện xa vắng thấy thương nhiều [hơn.\r\nKhi núi sông mịt mờ người trai nhuốm nhiều tuổi đời\r\nChuyện [xưa khép chặt vào lòng dù đi chinh chiến vẫn nhớ người tôi mến thương.\r\n\r\nTình yêu theo tháng năm xóa mờ trong mắt buồn\r\nNếu ta còn nhớ đến đêm thoáng trong mơ\r\nÐời anh là mây bốn phương trời còn miệt mài\r\nNgười đi xây tương lai dấu tâm tư thương ai\r\nNhư gió đưa mây che trăng khuất đêm dài\r\nTim tôi đơn côi biết nhau một lần rồi nhớ trọn đời.\r\n\r\nTôi muốn gửi những hình ảnh đời tôi và bọn tôi\r\nNhững người trẻ hôm nay đến những thương yêu hậu phương\r\nXin nhớ nhau như tình nhân tình nhân nhớ lúc người xa chưa về\r\n\r\nTôi xin đa tạ những người tình hôm nay\r\nĐã biết thương anh lính trẻ đường dài\r\nXin đa tạ cuộc đời đã cho tôi thấy mặt bạn [bè\r\nNhững người còn quanh tôi.\r\n\r\nMai đây không còn\r\nĐạn nổ trên cao xác thù trong chiến hào\r\nTôi ra đi đi từ nam về bắc\r\nGhé lại sông Hương thăm người thương thăm phố phường\r\nThăm Huế buồn vẳng nghe tiếng hò.\r\n\r\n* Ai cho tôi, ai cho tôi, ai cho tôi, tình yêu!', 1, 24, 24, 0, 0, 1477261598, NULL, 0, 'http://i.imgur.com/PBlm6Iv.jpg', 0, 0, '', '1\r\n00:00:01,850 --&gt; 00:00:04,720\r\nBài hát: Liên Khúc Tuấn Vũ 1\r\n\r\n2\r\n00:00:04,720 --&gt; 00:00:08,120\r\n(Ai cho tôi tình yêu)\r\n\r\n3\r\n00:00:08,120 --&gt; 00:00:11,300\r\nTrình bày: Tuấn Vũ\r\n\r\n4\r\n00:00:11,300 --&gt; 00:00:14,340\r\nLời bài hát: hopamviet\r\n\r\n5\r\n00:00:14,340 --&gt; 00:00:17,950\r\nHiệu chỉnh: Tuấn Nguyễn\r\n\r\n6\r\n00:00:17,950 --&gt; 00:00:23,280\r\nKaraoke: tuannvbg@gmail.com\r\n\r\n7\r\n00:00:23,280 --&gt; 00:00:33,960\r\nChúc bạn nghe nhạc vui vẻ\r\n\r\n8\r\n00:00:52,820 --&gt; 00:00:55,310\r\nAi cho tôi tình yêu\r\n\r\n9\r\n00:00:55,310 --&gt; 00:00:57,790\r\nđể làm duyên nụ cười\r\n\r\n10\r\n00:00:57,790 --&gt; 00:01:02,020\r\nTôi xin dâng vòng tay mở rộng\r\n\r\n11\r\n00:01:02,020 --&gt; 00:01:05,430\r\nVà đón người đi vào tim tôi\r\n\r\n12\r\n00:01:05,430 --&gt; 00:01:07,850\r\nBằng môi trên bờ môi\r\n\r\n13\r\n00:01:09,280 --&gt; 00:01:12,070\r\nNhưng biết chỉ là mơ\r\n\r\n14\r\n00:01:12,070 --&gt; 00:01:14,560\r\nNhưng  lòng nức nở\r\n\r\n15\r\n00:01:14,560 --&gt; 00:01:18,600\r\nThương còn đi yêu thì chưa đến\r\n\r\n16\r\n00:01:18,600 --&gt; 00:01:22,320\r\nNên gọi tên tình chưa đỗ bến\r\n\r\n17\r\n00:01:22,320 --&gt; 00:01:25,680\r\nNẻo mô mà tìm\r\n\r\n18\r\n00:01:26,290 --&gt; 00:01:30,290\r\nTám hướng bốn phương trời mây\r\n\r\n19\r\n00:01:30,290 --&gt; 00