--
-- Structure de la table `tgt_nhac_singer`
--

CREATE TABLE IF NOT EXISTS `tgt_nhac_singer` (
  `singer_id` int(10) NOT NULL AUTO_INCREMENT,
  `singer_name` varchar(255) CHARACTER SET utf8 NOT NULL,
  `singer_name_ascii` varchar(255) COLLATE latin1_general_ci NOT NULL,
  `singer_img` varchar(255) COLLATE latin1_general_ci NOT NULL,
  `singer_info` text CHARACTER SET utf8 NOT NULL,
  `singer_type` char(1) COLLATE latin1_general_ci DEFAULT NULL,
  `sub_id` int(11) NOT NULL,
  `singer_viewed` int(10) NOT NULL DEFAULT '0',
  `singer_hot` int(3) NOT NULL,
  PRIMARY KEY (`singer_id`),
  KEY `idx_singer_seach` (`singer_name_ascii`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1 COLLATE=latin1_general_ci AUTO_INCREMENT=237 ;

--
-- Contenu de la table `tgt_nhac_singer`
--

INSERT INTO `tgt_nhac_singer` (`singer_id`, `singer_name`, `singer_name_ascii`, `singer_img`, `singer_info`, `singer_type`, `sub_id`, `singer_viewed`, `singer_hot`) VALUES
(233, 'Ngọc Lan ft Lưu Hồng ft Huy Sinh', 'ngoc lan ft luu hong ft huy sinh', '', '', '1', 0, 0, 0),
(103, 'Khắc Việt', 'khac viet', 'http://img.news.zing.vn/img/694/t694399.jpg', 'Tên thật: Nguyễn Khắc Việt\r\nNgày sinh: 30/08/1987\r\nThể loại: Việt Nam, Nhạc Trẻ\r\nQuốc Gia: Việt Nam\r\n- Nickname: Việt Vui Vẻ (do fanclub dành tặng); \r\n\r\n- Đã tốt nghiệp Học viện Âm nhạc Quốc gia Việt Nam, khoa Thanh nhạc vào năm 2005; \r\n\r\n- Năm 2009, phát hành album online “Câu chuyện tình yêu”; \r\n\r\n- Đầu năm 2010, phát hành single và video clip “Như vậy nhé”; \r\n\r\n- Tính đến thời điểm hiện nay đã sáng tác hơn 100 ca khúc, trong đó có một số ca khúc được đông đảo khán giả trẻ yêu thích. \r\n\r\n- Sinh ra và lớn lên ở Yên Bái, trong một gia đình không có ai theo nghệ thuật, bố mẹ là công chức; sau khi học xong phổ thông, Khắc Việt cùng em ruột của mình thi đỗ Nhạc viện Hà Nội (Học viện Âm nhạc Quốc gia bây giờ) vào năm 2005. \r\n\r\n- Tốt nghiệp khoa Thanh nhạc, nhưng Khắc Việt lại bén duyên với con đường sáng tác khi cho ra mắt ca khúc đầu tay với tựa đề “Quên” vào năm 2009. Sau khi phát hành một thời gian ngắn, ca khúc “Quên” do chính Khắc Việt thể hiện đã trở thành một hit trên mạng vào thời gian đó và liên tục đứng vị trí cao tại các bảng xếp hạng âm nhạc online. Với thành công đó, Khắc Việt đã tiếp tục cho ra đời album online &quot;Câu chuyện tình yêu&quot; để chuẩn bị cho con đường ca hát chuyên nghiệp của mình. Đầu năm 2010, single “Như vậy nhé” được phát hành online và nhận được rất nhiều sự ủng hộ của khán giả, đặc biệt là cộng đồng mạng. Với chất giọng nam cao, sáng, đầy nội lực nhưng không kém phần tinh tế, Khắc Việt gây ấn tượng với khán giả bằng các ca khúc trữ tình, đậm chất pop-ballad trong thời gian qua. Có thể nói đây là một sở trường mà Khắc Việt đã biết khai thác đúng hướng. Không có thế mạnh về ngoại hình như nhiều ca sĩ hiện nay nhưng Khắc Việt tự tin với âm nhạc mang dấu ấn riêng của mình sẽ khiến cho khán giả “bắt tai”. Với âm nhạc, Khắc Việt đặt ra tiêu chí chú trọng phần nghe hơn là phần nhìn. \r\n\r\n- Bên cạnh ca hát thì sáng tác là niềm đam mê của Khắc Việt ngay từ khi bước chân vào con đường ca hát. Sở hữu hơn 100 ca khúc tính đến thời điểm bây giờ, cái tên Khắc Việt đang được nhiều ca sĩ thị trường tìm đến cộng tác cùng trong các dự án âm nhạc của họ. Hiện tại, Khắc Việt đang là sáng tác chính cho một số ca sĩ như Nam Cường, Cao Thái Sơn, Nhật Tinh Anh, Quỳnh Nga, nhóm The Men… Những sáng tác của Khắc Việt như: Yêu thương quay về, Bình yên nhé (Cao Thái Sơn); Phải là anh, Đoạn cuối con đường (Nam Cường); Chờ em trong đêm (The Men); Suy nghĩ trong anh (Duy Khoa)… là những bài hát đang nằm trong top hot các ca khúc được nghe nhiều nhất trên một số kênh âm nhạc hiện nay.', '1', 0, 0, 0),
(223, 'voatiengviet.com', 'voatiengviet.com', '', '', '2', 0, 0, 0),
(224, 'Đào Bá Lộc', 'dao ba loc', '', '', '1', 0, 0, 0),
(225, 'Tuấn Vũ', 'tuan vu', 'http://v2.cdn.nhac.vn/kv0puCNE4oNNfn7YhOpK/1468989761/v1/album/s1/0/0/204/209742.jpg', 'Ca sĩ Tuấn Vũ tên thật là Nguyễn Văn Tài (sinh ngày 16 tháng 12 năm 1959 tại Phan Thiết, Việt Nam) là một nam ca sĩ nổi tiếng người Việt sống ở hải ngoại. Quê quán tại xã Đồng Văn, huyện Thanh Chương, Nghệ An. \r\n\r\nAnh sang Mĩ năm 1979 và định cư ở San Francisco. Tuấn Vũ theo nghiệp ca sĩ từ đầu những năm 1980 và trở thành ca sĩ nhạc vàng nổi tiếng. Đến nay Tuấn Vũ đã thu âm trên dưới 1.400 bài hát thuộc nhiều thể loại. Hầu hết các nhạc phẩm làm nên tên tuổi của anh đều được hát trong giai đoạn từ năm 1994 trở về trước: Hoa sứ nhà nàng, Đôi mắt người xưa, Cô bé ngày xưa, Người yêu cô đơn, Giọt lệ đài trang, Phượng buồn, Tình khúc buồn, Bướm trắng, Ngày mai tôi về, Kỉ niệm một mùa hè, Đám cưới đầu xuân, Có thế thôi ... Rất nhiều liên khúc được Tuấn Vũ trình bày cũng được nhiều người biết đến. \r\n\r\nVào những thập niên 80 và 90 đi đâu trên mọi miền đất nước từ Bắc tới Nam người ta cũng nghe cũng văng vẳng giọng ca của anh. \r\n\r\nTuấn Vũ là 1 trong số ít ca sĩ được đông đảo số người hâm mộ cho tới ngày nay. Hiện nay tại Hà Nội và Tp Hồ Chí Minh có các câu lạc bộ fan Tuấn Vũ (FTV) duy trì hoạt động, gặp gỡ thường xuyên. Các fan của Tuấn Vũ hâm mộ anh không chỉ bởi các ca khúc bất hủ đã đi sâu vào lòng người mà còn bởi tính cách hòa đồng, chân thật và giản dị của anh \r\n\r\nTiếng hát của Tuấn Vũ một thời là thần tượng của thính giả trong nước Việt Nam cũng như tại hải ngoại. Đi đâu cũng nghe Tuấn Vũ, từ Sài Gòn ra Hà Nội, đâu đâu cũng nghe văng vẳng giọng ca của anh', '1', 0, 0, 0),
(226, 'Khang Lê', 'khang le', '', '', '1', 0, 0, 0),
(227, 'Lưu Chí Vỹ ft Lưu Ánh Loan ft Hồng Quyên ft Lý Diệu Linh', 'luu chi vy ft luu anh loan ft hong quyen ft ly dieu linh', '', '', '1', 0, 0, 0),
(228, 'Lưu Chí Vỹ', 'luu chi vy', '', '', '1', 0, 0, 0),
(229, 'Noo Phước Thịnh', 'noo phuoc thinh', 'http://image.mp3.zdn.vn/avatars/7/5/7504cf5e5d7f0d9c66e8ba631a4a4a8b_1474206905.jpg', 'Tên thật: Nguyễn Phước ThịnhNgày sinh: 18/12/1988Thể loại: Việt Nam, Nhạc TrẻQuốc Gia: Việt Nam Trước khi trở thành ca sỹ, Noo Phước Thịnh (Noo) là một người mẫu được giới teen yêu thích với mật độ xuất hiện nhiều trên các trang báo học đường. Sở hữu khuôn mặt điển trai, nụ cười tươi tắn và tính cách thân thiện dễ gần, chàng người mẫu này hứa hẹn sẽ còn thành công hơn nữa trong làng người mẫu. \r\n\r\nNhưng thật bất ngờ, ngay cả với Noo cũng không nghĩ là một ngày nào đó mình sẽ trở thành ca sỹ, thật tình cờ khi anh được người quản lý hiện tại là Tuấn Khanh phát hiện ra giọng hát của mình trong một show quảng cáo khi vô tình nghe Noo hát, anh nhận thấy rằng Noo rất có năng khiếu và có triển vọng trở thành ca sỹ. \r\n\r\nChính cuộc gặp gỡ này đã đưa Noo bắt đầu bước chân vào lĩnh vực ca hát.Noo, mặc dù rất yêu nhạc Trịnh nhưng anh lại hướng đến dòng nhạc trẻ dành cho giới teen, đó chính là cách để Noo đến được với khán giả nhanh nhất và thực tế chứng minh anh đã lựa chọn đúng và điều đó được khẳng định bằng những ca khúc được giới trẻ ưa chuộng như “Nếu”; “100% yêu em” và thành công hơn cả là ca khúc nhạc Hàn “Mất em”. Các ca khúc này đã đưa tên tuổi Noo luôn đứng đầu các bảng xếp hạng âm nhạc, được click nhiều nhất trên các trang nghe nhạc đồng thời dẫn đầu bảng xếp hạng tháng 10 và 11 trên thị trường nhạc chờ dành cho điện thoại di động . \r\n\r\nHơn nữa mặc dù mới tham gia vào lĩnh vực ca hát nhưng Noo sở hữu một lượng fans rất lớn, họ luôn ủng hộ anh trên con đường nghệ thuật mà Noo đã chọn.Để đáp lại tình cảm yêu mến của khán giả dành cho mình,Noo luôn nỗ lực không ngừng, làm việc nghiêm túc và sẽ được khẳng định bằng album đầu tay với chủ đề không tên rất lạ &quot;... &quot; Đây cũng là dấu để ngỏ dành cho sự đánh giá cảm nhận của khán giả. \r\n\r\nCác album đã phát hành: \r\n1. Album Ba chấm \r\n2. Single Đổi thay \r\n3. Single Nỗi nhớ đầy vơi \r\n4. Album &quot;lạc bước trong đêm&quot; phát hành ngày 21/11/2011 \r\n5. Mini album EM phát hành ngày 22/11/2012', '1', 0, 0, 0),
(230, 'Trương Quỳnh Anh', 'truong quynh anh', 'http://image.mp3.zdn.vn/avatars/1/7/170d632a3afa46e0b9699e3f23c3a553_1470125521.jpg', 'Tên thật: Đặng Trương Quỳnh AnhNgày sinh: 12/01/1989Thể loại: Việt Nam, Nhạc TrẻQuốc Gia: Việt Nam Một số phim đã tham gia: \r\n- Chuyện tình yêu \r\n- Sóng gió cuộc đời \r\n- KTX \r\n- Tình án \r\n- Một ngày không có em \r\n- Đồng hồ cát, \r\n- Truy tìm dấu vết \r\n- Cuộc chiến hoa hồng \r\n- Cầu vồng đơn sắc \r\n- Lặng Lẽ Yêu Em \r\n\r\nMột số ca khúc được yêu thích: \r\nHọa tâm (ca khúc trong phim Họa bì), Hoàng hôn ấm áp (với Vân Quang Long), Tiếng chuông gió, Cổ tích đời thường, Đơn côi…', '1', 0, 0, 0),
