(206, 'Hoàng Tôn', 'hoang ton', 'http://image.mp3.zdn.vn/thumb/165_165/avatars/c/8/c862ac8082618a7b03358a8a7cdafaee_1459745810.jpg', 'Tên thật: Nguyễn Hoàng Tôn\r\nNgày sinh: 15/11/1988\r\nThể loại: Việt Nam, Nhạc Trẻ\r\nQuốc Gia: Việt Nam \r\n\r\nNguyễn Hoàng Tôn \r\n\r\n- Sinh ngày: 15/11/1988 \r\n\r\n- Nhạc sỹ, ca sỹ, sản xuất âm nhạc \r\n\r\n- Đề cử Top 10 Làn sóng xanh 2011 \r\n\r\n- Top 10 Pepsi Talent Show 2010 \r\n\r\n- Top 2 nhóm Hát tự nhiên Vfresh', '1', 0, 0, 0),
(208, 'Nhiều Ca Sĩ', 'nhieu ca si', 'http://image.mp3.zdn.vn/avatars/7/3/73688444a73a76169d03b689a7e785cf_1404904575.jpg', 'Nhiều ca sĩ cùng thể hiện một ca khúc', '1', 0, 0, 0),
